/**
 * @description 求和
 * @param {number} a
 * @param {number} b
 * @returns {number} a 和 b 的和
 */
declare const sum: (a: number, b: number) => number;

/**
 * @description 求差
 * @param {number} a
 * @param {number} b
 * @returns {number} a 和 b 的差
 */
declare const sub: (a: number, b: number) => number;

/**
 * @description 求积
 * @param {number} a
 * @param {number} b
 * @returns {number} a 和 b 的积
 */
declare const mul: (a: number, b: number) => number;

/**
 * @description 求商
 * @param {number} a
 * @param {number} b
 * @returns {number} a 和 b 的商
 */
declare const div: (a: number, b: number) => number;

export { sum, sub, mul, div };
